import mongo from "mongodb"

let connection_string = "mongodb+srv://FiguriX:programeristoljeca@cluster0.fsnvs.mongodb.net/myFirstDatabase?retryWrites=true&w=majority";

let client = new mongo.MongoClient(connection_string, {
    userNewUrlParser: true,
    userUnifiedTopology: true
})

let db = null

export default () => {
    return new Promise((resolve, reject) =>{

        if (db && client.isConnected()){
            resolve(db)
        }
        client.connect(err => {
            if(err){
                reject("Dolo je do greke prilikom spajanja: " + err)
            }
            else{
                console.log("Uspjeno spajanje na bazu")
                db = client.db("figurix")
                resolve(db)
            }
        })
    })
}

